import Foundation
import libcmark

/// A node is a wrapper of a raw `CMarkNode` belonging to the abstract syntax tree
/// generated by cmark.

public protocol Node {
    /// The wrapped node.

    var cmarkNode: CMarkNode { get }

    /// The wrapped child nodes.

    var children: [Node] { get }
}

public extension Node {
    /// True iff the node has a sibling that succeeds it.

    var hasSuccessor: Bool {
        cmark_node_next(cmarkNode) != nil
    }

    /// Sequence of wrapped child nodes.

    var childSequence: ChildSequence {
        ChildSequence(node: cmarkNode)
    }
}

// MARK: - Helper extensions

public typealias CMarkNode = UnsafeMutablePointer<cmark_node>

public extension CMarkNode {
    /// Wraps the cmark node referred to by this pointer.

    func wrap() -> Node? {
        switch type {
        case CMARK_NODE_DOCUMENT: Document(cmarkNode: self)
        case CMARK_NODE_BLOCK_QUOTE: BlockQuote(cmarkNode: self)
        case CMARK_NODE_LIST: List(cmarkNode: self)
        case CMARK_NODE_ITEM: Item(cmarkNode: self)
        case CMARK_NODE_CODE_BLOCK: CodeBlock(cmarkNode: self)
        case CMARK_NODE_HTML_BLOCK: HtmlBlock(cmarkNode: self)
        case CMARK_NODE_CUSTOM_BLOCK: CustomBlock(cmarkNode: self)
        case CMARK_NODE_PARAGRAPH: Paragraph(cmarkNode: self)
        case CMARK_NODE_HEADING: Heading(cmarkNode: self)
        case CMARK_NODE_THEMATIC_BREAK: ThematicBreak(cmarkNode: self)
        case CMARK_NODE_TEXT: Text(cmarkNode: self)
        case CMARK_NODE_SOFTBREAK: SoftBreak(cmarkNode: self)
        case CMARK_NODE_LINEBREAK: LineBreak(cmarkNode: self)
        case CMARK_NODE_CODE: Code(cmarkNode: self)
        case CMARK_NODE_HTML_INLINE: HtmlInline(cmarkNode: self)
        case CMARK_NODE_CUSTOM_INLINE: CustomInline(cmarkNode: self)
        case CMARK_NODE_EMPH: Emphasis(cmarkNode: self)
        case CMARK_NODE_STRONG: Strong(cmarkNode: self)
        case CMARK_NODE_LINK: Link(cmarkNode: self)
        case CMARK_NODE_IMAGE: Image(cmarkNode: self)
        default: nil
        }
    }

    var parent: CMarkNode? {
        cmark_node_parent(self)
    }

    var type: cmark_node_type {
        cmark_node_get_type(self)
    }

    var literal: String? {
        String(cString: cmark_node_get_literal(self))
    }

    var fenceInfo: String? {
        String(cString: cmark_node_get_fence_info(self))
    }

    var headingLevel: Int {
        Int(cmark_node_get_heading_level(self))
    }

    var listType: cmark_list_type {
        cmark_node_get_list_type(self)
    }

    var listStart: Int {
        Int(cmark_node_get_list_start(self))
    }

    var url: String? {
        String(cString: cmark_node_get_url(self))
    }

    var title: String? {
        String(cString: cmark_node_get_title(self))
    }
}

private extension String {
    init?(cString: UnsafePointer<Int8>?) {
        guard let unwrapped = cString else { return nil }
        let result = String(cString: unwrapped)
        guard !result.isEmpty else { return nil }
        self = result
    }
}
